import { Component, OnInit, inject } from '@angular/core';
import { User } from 'src/app/Models/User';

import { USER_TOKEN } from 'src/app/app.module';
@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html'
})
export class UserDetailComponent implements on{
selectedUser: User;
userService = inject(USER_TOKEN);

ngOnInit(){
this.userService.OnUserDetailsClicked.subscribe((data: User) => {
this.selectedUser = data;

});
}
}
<div class="user-detail-container">
    <div>
        <img class="user-detail-avatar-image" [src]="'./assets/images/'+selectedUser.gender.toLowerCase()+'-avatar.png'">
    </div>
    <div class="user-detail-info">
        <h2>User Detail</h2>
        <p><b>Name:</b> {{ selectedUser.name }}</p>
        <p><b>Gender:</b> {{ selectedUser.gender }}</p>
        <p><b>Subscription Type:</b> {{ selectedUser.subType }}</p>
        <p><b>Status:</b> {{ selectedUser.status }}</p>
    </div>    
</div>
